import React from 'react';
import "./style.css"

function Body() {
    
    this.state = {value: 0}
    pokeTest = () => {
        this.setState(prevState => {
           return {count: prevState.count + 1}
        })
    }

    return (
        <div>
            <h2 className="score">Score: {this.state}</h2>
            <img className="pokeImage" onClick={() => pokeTest()} src={require("./images/pikachu.png")} height="200px" width="200px" alt="An adorable little Pikachu"></img>
            <img className="pokeImage" src={require("./images/charmander.png")} height="200px" width="200px" alt="An adorable little Charmander"></img>
            <img className="pokeImage" src={require("./images/squirtle.jpg")} height="200px" width="200px" alt="An adorable little Squirtle"></img>
            <img className="pokeImage" src={require("./images/bulbasaur.jpg")} height="200px" width="200px" alt="An adorable little Bulbasaur"></img>
            <img className="pokeImage" src={require("./images/eevee.jpg")} height="200px" width="200px" alt="An adorable little Eevee"></img>
        </div>
    )
}
  
export default Body;
